import React, { useState } from "react";
import {
  Form,
  Input,
  Button,
  Radio,
  Select,
  Cascader,
  DatePicker,
  InputNumber,
  TreeSelect,
  Switch,
  Alert,
  Row,
  Col,
  Upload,
  Icon,
  message,
} from "antd";

const Addlisting = () => {
  const [componentSize, setComponentSize] = useState("default");
  const [loading, setloading] = useState(false);
  const [imageUrl, setimageUrl] = useState("");

  const onFormLayoutChange = ({ size }) => {
    setComponentSize(size);
  };
  function getBase64(img, callback) {
    const reader = new FileReader();
    reader.addEventListener("load", () => callback(reader.result));
    reader.readAsDataURL(img);
  }

  function beforeUpload(file) {
    const isJpgOrPng = file.type === "image/jpeg" || file.type === "image/png";
    if (!isJpgOrPng) {
      message.error("You can only upload JPG/PNG file!");
    }
    const isLt2M = file.size / 1024 / 1024 < 2;
    if (!isLt2M) {
      message.error("Image must smaller than 2MB!");
    }
    return isJpgOrPng && isLt2M;
  }

  const handleChange = (info) => {
    if (info.file.status === "uploading") {
      setloading(true);
      return;
    }
    if (info.file.status === "done") {
      // Get this url from response in real world.
      getBase64(info.file.originFileObj, (imageUrl) => {
        setimageUrl(imageUrl);
        setloading(false);
      });
    }
  };

  return (
    <>
      <Alert message="PROPERTY TYPE AND LOCATION" type="success" />
      <Form
        labelCol={{
          span: 4,
        }}
        wrapperCol={{
          span: 14,
        }}
        layout="horizontal"
        initialValues={{
          size: componentSize,
        }}
        onValuesChange={onFormLayoutChange}
        size={componentSize}
      >
        <Form.Item label="Form Size" name="size">
          <Radio.Group>
            <Radio.Button value="small">Sale</Radio.Button>
            <Radio.Button value="default">Rent</Radio.Button>
          </Radio.Group>
        </Form.Item>

        <Form.Item label="Form Size" name="size">
          <Radio.Group>
            <Radio.Button value="home">Homes</Radio.Button>
            <Radio.Button value="plots">Plots</Radio.Button>
            <Radio.Button value="commercial">Commercial</Radio.Button>
          </Radio.Group>
        </Form.Item>
        <Form.Item label="Select city">
          <Select>
            <Select.Option value="demo" selected>
              Islamabad
            </Select.Option>
            <Select.Option value="demo">Rawalpindi</Select.Option>
            <Select.Option value="demo">Karachi</Select.Option>
          </Select>
        </Form.Item>
        <Form.Item label="Location">
          <Input />
        </Form.Item>

        <Form.Item label="TreeSelect">
          <TreeSelect
            treeData={[
              {
                title: "Light",
                value: "light",
                children: [
                  {
                    title: "Bamboo",
                    value: "bamboo",
                  },
                ],
              },
            ]}
          />
        </Form.Item>
        <Form.Item label="Cascader">
          <Cascader
            options={[
              {
                value: "zhejiang",
                label: "Zhejiang",
                children: [
                  {
                    value: "hangzhou",
                    label: "Hangzhou",
                  },
                ],
              },
            ]}
          />
        </Form.Item>
        <Form.Item label="DatePicker">
          <DatePicker />
        </Form.Item>
        <Form.Item label="InputNumber">
          <InputNumber />
        </Form.Item>
        <Form.Item label="Switch">
          <Switch />
        </Form.Item>
        <Form.Item label="Button">
          <Button>Button</Button>
        </Form.Item>
      </Form>

      <Alert message="PROPERTY DETAILS" type="success" />
      <Form.Item label="Property Title">
        <Input />
      </Form.Item>

      <Form.Item label="Property Details">
        <Input />
      </Form.Item>

      <Form.Item label="All inclusive Price">
        <Input />
      </Form.Item>

      <Row>
        <Col lg={12}>
          <Form.Item label="Land Area">
            <Input />
          </Form.Item>
        </Col>
        <Col lg={12}>
          <Form.Item label="Square feet">
            <Select>
              <Select.Option value="1" selected>
                1
              </Select.Option>
              <Select.Option value="2">2</Select.Option>
              <Select.Option value="2">3</Select.Option>
            </Select>
          </Form.Item>
        </Col>
      </Row>
      <Form.Item label="Bedrooms">
        <Select>
          <Select.Option value="demo" selected>
            Islamabad
          </Select.Option>
          <Select.Option value="1">1</Select.Option>
          <Select.Option value="2">2</Select.Option>
        </Select>
      </Form.Item>
      <Form.Item label="Bathrooms">
        <Select>
          <Select.Option value="demo" selected>
            Islamabad
          </Select.Option>
          <Select.Option value="1">1</Select.Option>
          <Select.Option value="2">2</Select.Option>
        </Select>
      </Form.Item>
      <Form.Item label="Expires After">
        <Select>
          <Select.Option value="6 Months" selected>
            6 Months
          </Select.Option>
          <Select.Option value="1 Year">1 Year</Select.Option>
        </Select>
      </Form.Item>
      <Upload
        name="avatar"
        listType="picture-card"
        className="avatar-uploader"
        showUploadList={false}
        action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
        beforeUpload={beforeUpload}
        onChange={handleChange}
      >
        {imageUrl ? (
          <img src={imageUrl} alt="avatar" style={{ width: "100%" }} />
        ) : (
          <div>
            <Icon type={loading ? "loading" : "plus"} />
            <div className="ant-upload-text">Upload</div>
          </div>
        )}
      </Upload>

      <Alert message="CONTACT DETAILS" type="success" />

      <Form.Item label="User Name ">
        <Input />
      </Form.Item>

      <Form.Item label="Password">
        <Input />
      </Form.Item>
    </>
  );
};

export default Addlisting;
