import React, { useState, useEffect } from "react";
import {
  Form,
  Input,
  Button,
  Radio,
  Select,
  Cascader,
  DatePicker,
  InputNumber,
  TreeSelect,
  Switch,
  Alert,
  Row,
  Col,
  Upload,
  Icon,
  message,
  Card,
  Checkbox,
} from "antd";
import { padding } from "styled-system";
import axios from "axios";
import {
  getPropType,
  getPropSubType,
  getCities,
  getLocations,
  getCurrency,
  getArea,
} from "../../settings/service";

const AddFeature = () => {
  const [loading, setloading] = useState(false);

  return (
    <Card style={{ paddingLeft: "10rem", paddingRight: "10rem" }}>
      <Form
        labelCol={{
          span: 4,
        }}
        wrapperCol={{
          span: 14,
        }}
        layout="horizontal"
        style={{ marginTop: "20px" }}
      >
        <Alert message="PROPERTY FEATURES" type="success" />
        <h3>Main Features</h3>
        <Form.Item label="Built in year">
          <Input />
        </Form.Item>
        <Form.Item label="View">
          <Input />
        </Form.Item>
        <Form.Item label="Parking Spaces">
          <Input />
        </Form.Item>
        <Form.Item label="Double Glazed Windows">
          <Checkbox></Checkbox>
        </Form.Item>
        <Form.Item label="Central Air Conditioning">
          <Checkbox></Checkbox>
        </Form.Item>
        <Form.Item label="Central Heating">
          <Checkbox></Checkbox>
        </Form.Item>
      </Form>
    </Card>
  );
};

export default AddFeature;
