const { get } = require("js-cookie");
import axios from "axios";
import { baseURL } from "./config";
export const getPropType = async () => {
  const response = await axios.get(`${baseAuth}/categorys`, {
    headers: {
      Authorization: "Basic YXBuYTphcG5hZ2hhcjEyMzQ=",
      Cookie: "JSESSIONID=E2A8BF2571BA6B875803BD545D43BBD6",
    },
  });
  return response;
};

export const getPropSubType = async () => {
  const response = await axios.get(`${baseAuth}/proptypes`, {
    headers: {
      Authorization: "Basic YXBuYTphcG5hZ2hhcjEyMzQ=",
      Cookie: "JSESSIONID=E2A8BF2571BA6B875803BD545D43BBD6",
    },
  });
  return response;
};

export const getCities = async () => {
  const response = await axios.get(`${baseAuth}/citys`, {
    headers: {
      Authorization: "Basic YXBuYTphcG5hZ2hhcjEyMzQ=",
      Cookie: "JSESSIONID=E2A8BF2571BA6B875803BD545D43BBD6",
    },
  });
  return response;
};

export const getLocations = async (cityid) => {
  const response = await axios.get(`${baseAuth}/locations/${cityid}`, {
    headers: {
      Authorization: "Basic YXBuYTphcG5hZ2hhcjEyMzQ=",
      Cookie: "JSESSIONID=E2A8BF2571BA6B875803BD545D43BBD6",
    },
  });
  return response;
};

export const getCurrency = async () => {
  const response = await axios.get(`${baseAuth}/currencys`, {
    headers: {
      Authorization: "Basic YXBuYTphcG5hZ2hhcjEyMzQ=",
      Cookie: "JSESSIONID=E2A8BF2571BA6B875803BD545D43BBD6",
    },
  });
  return response;
};

export const getArea = async () => {
  const response = await axios.get(`${baseAuth}/areaunits`, {
    headers: {
      Authorization: "Basic YXBuYTphcG5hZ2hhcjEyMzQ=",
      Cookie: "JSESSIONID=E2A8BF2571BA6B875803BD545D43BBD6",
    },
  });
  return response;
};
